import React from 'react';

function Section({ heading, subHeading, content, btn }) {
  const renderSections = () => {
    return content.map((item, index) => (
      <div key={index}>
        <h1 className="heading">{item.heading}</h1>
        <div className="row">
          <div className="content">
            <h3>{item.subHeading}</h3>
            <p>{item.content}</p>
            {item.btn === "Read more" && <button className="btn">{item.btn}</button>}
          </div>
        </div>
      </div>
    ));
  };

  return <section id="about" className="about">{renderSections()}</section>;
}

export default Section;
