import React from 'react';

function Section({props}) 
{
  const { content: paragraph } = props;
  let button = null;

  button = props.btn === "Read more" ? <button className="btn">{props.btn}</button> : null;

  return (
    <section id="about" className="about">
     <div>
      {aboutUsData.map((data, index) => (
        <div key={index}>
          <h1>{data.heading}</h1>
          <h2>{data.subHeading}</h2>
          <p>{data.content}</p>
          <button>{data.btn}</button>
        </div>
      ))}
    </div>
    </section>
  );
}

export default Section;
