import React from 'react';

function Section(props) {
  // Destructure props to access data
  const { data } = props;

  return (
    <div>
      {/* Iterate over data array */}
      {data.map((item, index) => (
        <section key={index} id={item.id} className={item.className}>
          <h1 className="heading">{item.heading}</h1>
          <div className="row">
            <div className="content">
              <h3>{item.subHeading}</h3>
              <p>{item.content}</p>
              {/* Render button if btn is "Read more" */}
              {item.btn === "Read more" && <button className="btn">{item.btn}</button>}
            </div>
          </div>
        </section>
      ))}
    </div>
  );
}

export default Section;
